import React, { useState } from "react";
import CalendarSelector from "./CalendarSelector";

function Note() {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [body, setBody] = useState("");
  function handleDateChange(date) {
    setSelectedDate(date);
  }

  function handleTodayButtonClick() {
    setSelectedDate(new Date());
  }

  function handleBodyChange(event) {
    setBody(event.target.value);
  }

  function handleKeyPress(event) {
    if (event.which === 13 && !event.shiftKey) {
      event.preventDefault();
      setBody(body + "\n");
    }
  }

  return (  
    <div className="note-page">
      <div className="note-head">
        <div style={{ display: "flex", flexDirection: "column"}}>
          <input type="text" id="title" placeholder="Untitled" style={{ border: "none", outline: "none" }} />
          <small>
            <CalendarSelector selectedDate={selectedDate} handleDateChange={handleDateChange} handleTodayButtonClick={handleTodayButtonClick} />
          </small>
        </div>
        <div>
          <button id="saveButton">Save</button>
          <button id="deleteButton">Delete</button>
        </div>
      </div>
      <div className="note-buttons">
        <p>button</p>
      </div>
      <div className="note-body">
      <input type="text" id="body" placeholder="Start typing..." value={body} onChange={handleBodyChange} onKeyPress={handleKeyPress} />
      </div>
    </div>
  );
}


export default Note;
