import React, { useState, useEffect } from "react";
import CalendarSelector from "./CalendarSelector";
import { useNavigate, useParams } from 'react-router-dom';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

function EditNote({ notes, activeNote, onNoteChange, onDeleteNote }) {
  const navigate = useNavigate();
  const { noteId } = useParams();
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [note, setNote] = useState(notes.find((note) => note.id === activeNote));
  

  useEffect(() => {
    setNote(notes.find((note) => note.id === activeNote));
  }, [activeNote, notes]);

  function handleDateChange(date) {
    setSelectedDate(date);
    setNote({ ...note, lastModified: new Date().toLocaleString() });
  }

  function handleSaveButton() {
    onNoteChange(note);
    navigate(`/${noteId}`);
  }

  function handleDeleteButton() {
    const answer = window.confirm("Are you sure?");
    if (answer) {
      onDeleteNote(activeNote);
      navigate('/');
    }
  }

  function handleNoteChange(event) {
    const { name, value } = event.target;
    setNote({ ...note, [name]: value, lastModified: new Date().toLocaleString() });
  }

  return (
    <div className="note-page">
      <div className="note-head">
        <div className="temp" style={{ display: "flex", flexDirection: "column" }}>
          {activeNote && (
            <input type="text" id="title" name="title" value={note.title} onChange={handleNoteChange} style={{ border: "none", outline: "none" }} />
          )}
          <small>
            <CalendarSelector selectedDate={selectedDate} handleDateChange={handleDateChange} />
          </small>
        </div>
        <div>
          <button id="SaveButton" onClick={handleSaveButton}>Save</button>
          <button id="deleteButton" onClick={handleDeleteButton}>Delete</button>
        </div>
      </div>
      <div className="note-body">
        <ReactQuill 
          value={note.body} 
          onChange={(value) => handleNoteChange({ target: { name: 'body', value }})} 
          modules={{
            toolbar: [
              [{'font': []}],
              [{size: []}],
              ['bold', 'italic', 'underline',],
              ['link'],
              [{'list': 'ordered'}, {'list': 'bullet'}],
              ['clean']]
          }}
        />
      </div>
    </div>
  );
}

export default EditNote;
