import React, { useState, useEffect } from 'react';

function EditNote({ notes, activeNote, onNoteChange, onDeleteNote }) {
  const [noteTitle, setNoteTitle] = useState('');
  const [noteBody, setNoteBody] = useState('');

  useEffect(() => {
    const note = notes.find((note) => note.id === activeNote);
    if (note) {
      setNoteTitle(note.title);
      setNoteBody(note.body);
    }
  }, [notes, activeNote]);

  function handleNoteTitleChange(event) {
    setNoteTitle(event.target.value);
  }

  function handleNoteBodyChange(event) {
    setNoteBody(event.target.value);
  }

  function handleNoteDelete() {
    onDeleteNote && onDeleteNote(activeNote);
  }

  function handleNoteSave() {
    const updatedNote = {
      id: activeNote,
      title: noteTitle,
      body: noteBody,
      lastModified: Date.now(),
    };
    onNoteChange && onNoteChange(updatedNote);
  }

  return (
    <div className='note-editor'>
      <input
        className='note-title-input'
        type='text'
        placeholder='Note title'
        value={noteTitle}
        onChange={handleNoteTitleChange}
      />
      <textarea
        className='note-body-input'
        placeholder='Start typing...'
        value={noteBody}
        onChange={handleNoteBodyChange}
      />
      <div className='button-row'>
        <button className='button' onClick={handleNoteDelete}>
          Delete
        </button>
        <button className='button' onClick={handleNoteSave}>
          Save
        </button>
      </div>
    </div>
  );
}

export default EditNote;
