import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function Note() {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [showCalendar, setShowCalendar] = useState(false);

  function handleDateChange(date) {
    setSelectedDate(date);
  }

  return (
    <div className="note-page">
      <div className="note-head">
        <input type="text" id="title" placeholder="Untitled" style={{ border: "none", outline: "none" }} />
        <br></br>
        <small id="date">
          <button onClick={() => setShowCalendar(!showCalendar)}>ðŸ“…</button>
          {selectedDate.toLocaleDateString()}, {selectedDate.toLocaleTimeString("en-US")}
          {showCalendar && (
            <DatePicker
              selected={selectedDate}
              onChange={handleDateChange}
              showTimeSelect
              dateFormat="yyyy-MM-dd h:mm:ss aa"
            />
          )}
        </small>
      </div>
      <div className="note-buttons">
        <p>button</p>
      </div>
      <div className="note-body">
        <p>body</p>
      </div>
    </div>
  );
}

export default Note;
