import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function Note() {
  const [selectedDate, setSelectedDate] = useState(new Date());

  function handleDateChange(date) {
    setSelectedDate(date);
  }

  return (
    <div className="note-page">
      <div className="note-head">
        <input type="text" id="title" placeholder="Untitled" style={{ border: "none", outline: "none" }} />
        <br></br>
        <small id="date">
          <DatePicker
            selected={selectedDate}
            onChange={handleDateChange}
            showTimeSelect
            timeFormat="HH:mm:ss"
            timeIntervals={1}
            dateFormat="yyyy-MM-dd"
            timeCaption="Time"
            customTimeInput={<TimeInput />}
          /> 
        </small>
      </div>
      <div className="note-buttons">
        <p>button</p>
      </div>
      <div className="note-body">
        <p>body</p>
      </div>
    </div>
  );
}

function TimeInput(props) {
  const { date, onChange } = props;
  const hour = date.getHours();
  const minute = date.getMinutes();
  const second = date.getSeconds();

  function handleHourChange(e) {
    const value = parseInt(e.target.value, 10);
    const newDate = new Date(date);
    newDate.setHours(value);
    onChange(newDate);
  }

  function handleMinuteChange(e) {
    const value = parseInt(e.target.value, 10);
    const newDate = new Date(date);
    newDate.setMinutes(value);
    onChange(newDate);
  }

  function handleSecondChange(e) {
    const value = parseInt(e.target.value, 10);
    const newDate = new Date(date);
    newDate.setSeconds(value);
    onChange(newDate);
  }

  return (
    <div className="time-input">
      <label htmlFor="hour">Hour</label>
      <input type="number" id="hour" name="hour" min="0" max="23" value={hour} onChange={handleHourChange} />

      <label htmlFor="minute">Minute</label>
      <input type="number" id="minute" name="minute" min="0" max="59" value={minute} onChange={handleMinuteChange} />

      <label htmlFor="second">Second</label>
      <input type="number" id="second" name="second" min="0" max="59" value={second} onChange={handleSecondChange} />
    </div>
  );
}

export default Note;
