  }

  return (
    <div className='container'>
      <div className='top'>
        <div className='button'>
          <button id="list">â‰¡</button>
        </div>
        <div className='name'>
          <h1>Lotion</h1>
          <small>Like Notion, but worse.</small>
        </div>
      </div>
      <div className='page'>
        <div className='list'>
          <NoteList 
            notes={notes} 
            onAddNote={handleAddNote} 
            activeNote={activeNote} 
            onNoteClick={handleNoteClick} 
          />
        </div>
        {activeNote ? (
          <Note notes={notes} activeNote={activeNote} onDeleteNote={handleDeleteNote} onNoteChange={handleNoteChange} />
        ) : (
          <div className='no-note-selected'>No note selected</div>
        )}
          {activeNote && (
          <EditNote notes={notes} activeNote={activeNote} onNoteChange={handleNoteChange} onDeleteNote={handleDeleteNote} />
        )}

      </div>
    </div>                  
  );
}

export default Layout;


