function NoteList({notes, onAddNote}){
  return (
    <div className='notebar'>
      <div className='list-top'>
        <h2>Notes</h2>
        <button className="add" onClick={onAddNote}>+</button>
      </div>
      <div className='list-notes'>
        {notes.map((note) => (
          <div className='list-note'>
            <div className='note-title'>
              <strong>{note.title}</strong>
              <small>{note.lastModified && new Date(note.lastModified).toLocaleDateString("en-GB",{
                hour: "2-digit",
                minute: "2-digit",
              })}</small>
            </div>
            <p>{note.body && note.body.substr(0,100) + "..."}</p> //play around with this
          </div> 
         ))}
      </div>
    </div>
  );
}

export default NoteList;
