import React from "react";
import { useState } from "react";

function Toolbar({ handleButtonClick }) {
  return (
    <div style={{ display: 'flex' }}>
      <button onClick={() => handleButtonClick('bold')} style={{ background: 'transparent', border: 'none' }}>
        <b>B</b>
      </button>
      <button onClick={() => handleButtonClick('italic')} style={{ background: 'transparent', border: 'none' }}>
        <i>I</i>
      </button>
      <button onClick={() => handleButtonClick('underline')} style={{ background: 'transparent', border: 'none' }}>
        <u>U</u>
      </button>
      <button onClick={() => handleButtonClick('link')} style={{ background: 'transparent', border: 'none' }}>
        <span style={{ textDecoration: 'underline' }}>Link</span>
      </button>
      <button onClick={() => handleButtonClick('ordered-list')} style={{ background: 'transparent', border: 'none' }}>
        <ol>
          <li>1.</li>
        </ol>
      </button>
      <button onClick={() => handleButtonClick('unordered-list')} style={{ background: 'transparent', border: 'none' }}>
        <ul>
          <li>&bull;</li>
        </ul>
      </button>
      <button onClick={() => handleButtonClick('remove-formatting')} style={{ background: 'transparent', border: 'none' }}>
        <span style={{ textDecoration: 'line-through' }}>T</span>
      </button>
    </div>
  );
}

function Typing(props) {
  const [text, setText] = useState('');

  function handleButtonClick(formatting) {
    switch (formatting) {
      case 'bold':
        setText(prevText => `${prevText}<b></b>`);
        break;
      case 'italic':
        setText(prevText => `${prevText}<i></i>`);
        break;
      case 'underline':
        setText(prevText => `${prevText}<u></u>`);
        break;
      case 'link':
        const link = prompt('Enter the link:');
        setText(prevText => `${prevText}<a href="${link}"></a>`);
        break;
      case 'ordered-list':
        setText(prevText => `${prevText}<ol><li></li></ol>`);
        break;
      case 'unordered-list':
        setText(prevText => `${prevText}<ul><li></li></ul>`);
        break;
      case 'remove-formatting':
        setText('');
        break;
      default:
        break;
    }
  }

  function handleTextChange(event) {
    setText(event.target.value);
  }

  return (
    <div>
      <Toolbar handleButtonClick={handleButtonClick} />
      <textarea value={text} onChange={handleTextChange} />
    </div>
  );
}

export default Typing;
