import React from "react";


function Typing({ onFontChange, onBoldClick, onItalicClick, onUnderlineClick, onLinkClick, onOrderedListClick, onUnorderedListClick }) {
    return (
      <>
        <div className="note-buttons">
          <select onChange={onFontChange}>
            <option value="">Font</option>
            <option value="Arial">Arial</option>
            <option value="Calibri">Calibri</option>
            <option value="Times New Roman">Times New Roman</option>
          </select>
          <button onClick={onBoldClick}>B</button>
          <button onClick={onItalicClick}>I</button>
          <button onClick={onUnderlineClick}>U</button>
          <button onClick={onLinkClick}>Link</button>
          <button onClick={onOrderedListClick}>1.</button>
          <button onClick={onUnorderedListClick}>-</button>
        </div>
        <textarea id="body" className="note-body" placeholder="Your Note Here"/>
      </>
    );
  }

export default Typing;
