import React, { useState } from "react";
import Typing from "./Typing";

function EditButtons() {
  const [fontFamily, setFontFamily] = useState("Arial");
  const [selectedText, setSelectedText] = useState("");

  const handleChange = (event) => {
    setFontFamily(event.target.value);
  };

  const handleBoldClick = () => {
    const body = document.getElementById("body");
    const startIndex = body.selectionStart;
    const endIndex = body.selectionEnd;
    const text = body.value;

    if (startIndex !== endIndex) {
      const boldText = "<b>" + text.substring(startIndex, endIndex) + "</b>";
      const newBody = text.substring(0, startIndex) + boldText + text.substring(endIndex);
      setSelectedText("");
      body.value = newBody;
    }
  };

  return (
    <div>
      <select value={fontFamily} onChange={handleChange}>
        <option value="Arial">Arial</option>
        <option value="Helvetica">Helvetica</option>
        <option value="Times New Roman">Times New Roman</option>
        <option value="Courier New">Courier New</option>
        <option value="Verdana">Verdana</option>
        <option value="Georgia">Georgia</option>
        <option value="Palatino">Palatino</option>
        <option value="Garamond">Garamond</option>
        <option value="Bookman">Bookman</option>
        <option value="Comic Sans MS">Comic Sans MS</option>
        <option value="Trebuchet MS">Trebuchet MS</option>
      </select>
      <button id="boldButton" style={{ fontWeight: "bold" }} onClick={handleBoldClick}>
        B
      </button>
      <button id="italicButton" style={{ fontStyle: "italic" }}>
        I
      </button>
      <button id="underlineButton" style={{ textDecoration: "underline" }}>
        U
      </button>
      <p style={{ fontFamily }}>Sample Text</p>
      <Typing selectedText={selectedText} setSelectedText={setSelectedText} />
    </div>
  );
}

export default EditButtons;
