import React, { useState } from "react";

const CalendarSelector = () => {
  const [date, setDate] = useState(
    new Date().toLocaleString("en-US", {
      hour12: true,
      year: "numeric",
      month: "2-digit",
      day: "2-digit",
      hour: "2-digit",
      minute: "2-digit",
      second: "2-digit"
    })
  );

  const handleDateChange = (e) => {
    setDate(
      new Date(e.target.value).toLocaleString("en-US", {
        hour12: true,
        year: "numeric",
        month: "2-digit",
        day: "2-digit",
        hour: "2-digit",
        minute: "2-digit",
        second: "2-digit"
      })
    );
  };

  return (
    <div>
      <label htmlFor="date-input"></label>
      <input
        id="date-input"
        type="datetime-local"
        value={date}
        style={{ border: "none", outline: "none" }}
        onChange={handleDateChange}
      />
    </div>
  );
};

export default CalendarSelector;
