import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function Note() {
  const [selectedDate, setSelectedDate] = useState(new Date());

  function handleDateChange(date) {
    setSelectedDate(date);
  }

  function handleTodayClick() {
    setSelectedDate(new Date());
  }

  const todayButtonLabel = `Today (${selectedDate.toLocaleDateString()} ${selectedDate.toLocaleTimeString()})`;

  const todayButton = (
    <button className="today-button" onClick={handleTodayClick}>
      {todayButtonLabel}
    </button>
  );

  return (
    <div className="note-page">
      <div className="note-head">
        <input type="text" id="title" placeholder="Untitled" style={{ border: "none", outline: "none" }} />
        <br />
        <small id="date">
          <DatePicker
            selected={selectedDate}
            onChange={handleDateChange}
            showTimeSelect
            timeFormat="HH:mm:ss"
            timeIntervals={1}
            dateFormat="yyyy-MM-dd h:mm:ss aa"
            customInput={<div>{todayButton}</div>}
            todayButton="Today"
          />
        </small>
      </div>
      <div className="note-buttons">
        <p>button</p>
      </div>
      <div className="note-body">
        <p>body</p>
      </div>
    </div>
  );
}

export default Note;
