import React, { useState } from "react";

const CalendarSelector = ({ selectedDate, handleDateChange, handleTodayButtonClick }) => {
  const [date, setDate] = useState(selectedDate.toISOString().slice(0, 19));

  const handleTodayClick = () => {
    const today = new Date();
    const localOffset = today.getTimezoneOffset() * 60000; // offset in milliseconds
    const localDate = new Date(today.getTime() - localOffset);
    setDate(localDate.toISOString().slice(0, 19));
    handleTodayButtonClick(localDate);
  };

  const handleInputChange = (e) => {
    setDate(e.target.value);
    handleDateChange(new Date(e.target.value));
  };

  return (
    <div>
      <label htmlFor="date-input"></label>
      <input
        id="date-input"
        type="datetime-local"
        value={date}
        style={{ border: "none", outline: "none", padding: "0" }}
        onChange={handleInputChange}
      />
      <button onClick={handleTodayClick}>Today</button>
    </div>
  );
};

export default CalendarSelector;
