import React from 'react';

function App() {
  return <div className='container'>
    <div className='top'>
      <div className='button'>
        <button id="list">â‰¡</button>
      </div>
      <div className='name'>
        <h1>Lotion</h1>
        <small>Like Notion, but worse.</small>
      </div>
    </div>
    <div className='page'>
      <div className='list'>
        <div className='list-top'>
          <div className='add'><button>+</button></div>
          <h2>Notes</h2>
        </div>
        <NoteList />
      </div>
      <div className='note-page'>
      </div>
    </div>
  </div>;
}

const NoteList = () => {
  return (
    < div className='note-list' >
      <Note />
      <Note />
      <Note />
      <Note />
    </div>
  )
} 

const Note = () => {
  const options = { year: 'numeric', month: 'long', day: 'numeric' };
  const currentDate = new Date().toLocaleDateString('en-US', options);
  const timeOptions = { hour: 'numeric', minute: 'numeric', hour12: true };
  const currentTime = new Date().toLocaleTimeString('en-US', timeOptions);
  return (
    <div className='note'>
      <div class='note-title'>
        <big>Untitled</big><br></br>
        <small className='today-date'>{currentDate} at   {currentTime}</small>
      </div>
      <span>...</span>
    </div>
  )
}



export default App;
