import React, { useState } from "react";
import Typing from "./Typing";

function EditNote({ note, onSave, onCancel }) {
  const [title, setTitle] = useState(note.title);

  function handleTitleChange(event) {
    setTitle(event.target.value);
  }

  function handleSave() {
    const updatedNote = { ...note, title, body: document.querySelector('.note-body').innerHTML };
    onSave(updatedNote);
  }

  return (
    <div>
      <input value={title} onChange={handleTitleChange} placeholder="Untitled note" />
      <Typing defaultValue={note.body} />
      <div className="note-actions">
        <button onClick={handleSave}>Save</button>
        <button onClick={onCancel}>Cancel</button>
      </div>
    </div>
  );
}

export default EditNote;
