import React, { useState, useEffect } from "react";
import CalendarSelector from "./CalendarSelector";
import { useNavigate, useParams } from 'react-router-dom';
import ReactQuill from 'react-quill';

function EditNote({ notes, activeNote, onNoteChange, onDeleteNote }) {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [note, setNote] = useState(notes.find((note) => note.id === activeNote));
  const navigate = useNavigate();
  const { noteId } = useParams();

  useEffect(() => {
    setNote(notes.find((note) => note.id === activeNote));
  }, [activeNote, notes]);

  useEffect(() => {
    if (editorRef.current) {
      const editor = editorRef.current.getEditor();
      editor.format('font', 'sans-serif');
      editor.format('size', '16px');
      editor.format('bold', false);
      editor.format('italic', false);
      editor.format('underline', false);
      editor.clipboard.addMatcher(Node.ELEMENT_NODE, (node, delta) => {
        const attrs = {};
        const style = node.getAttribute('style');
        if (style) {
          attrs.style = style;
        }
        if (node.tagName === 'A') {
          attrs.href = node.getAttribute('href');
          return { ops: [{ insert: node.innerText, attributes: attrs }] };
        }
        return delta;
      });
      editor.on('text-change', () => {
        setBody(editorRef.current.getEditor().root.innerHTML);
      });
    }
  }, []);

  function handleDateChange(date) {
    setSelectedDate(date);
    setNote({...note, lastModified: new Date().toLocaleString()});
  }

  function handleSaveButton() {
    onNoteChange(note);
    navigate(`/${noteId}`);
  }

  function handleDeleteButton() {
    onDeleteNote(note.id);
    navigate('/');
  }

  function handleNoteChange(event) {
    const { name, value } = event.target;
    setNote({...note, [name]: value, lastModified: new Date().toLocaleString()});
  }

  return (  
    <div className="note-page">
      <div className="note-head">
        <div className="temp" style={{ display: "flex", flexDirection: "column"}}>
          {activeNote && (
            <input type="text" id="title" name="title" value={note.title} onChange={handleNoteChange} style={{ border: "none", outline: "none" }} />
          )}
          <small>
            <CalendarSelector selectedDate={selectedDate} handleDateChange={handleDateChange} />
          </small>
        </div>
        <div>
          <button id="SaveButton" onClick={handleSaveButton}>Save</button>
          <button id="deleteButton" onClick={handleDeleteButton}>Delete</button>
        </div>
      </div>
      <div className="note-body">
        <textarea id="body" name="body" value={note.body} onChange={handleNoteChange} style={{ border: "none", outline: "none", width: "100%", height: "calc(100% - 35px)", resize: "none" }}></textarea>
      </div>
    </div>
  );
}

export default EditNote;
