import React from 'react';
import { useNavigate } from 'react-router-dom';

function Note({ notes, onDeleteNote, activeNote, onNoteChange }) {
  const navigate = useNavigate();
  const note = notes.find((note) => note.id === activeNote);

  function handleEditClick() {
    navigate(`/${activeNote}/edit`);
  }

  function handleDeleteClick() {
    onDeleteNote(activeNote);
  }

  function handleNoteChange(event) {
    const { name, value } = event.target;
    onNoteChange(activeNote, { ...note, [name]: value });
  }

  return (
    <div className="note-page">
      <div className="note-head">
        <input type="text" id="title" value={note.title} name="title" onChange={handleNoteChange} />
        <small>{note.lastModified}</small>
        <div>
          <button id="editButton" onClick={handleEditClick}>Edit</button>
          <button id="deleteButton" onClick={handleDeleteClick}>Delete</button>
        </div>
      </div>
      <div className="note-body">
        <textarea id="body" value={note.body} name="body" onChange={handleNoteChange} />
      </div>
    </div>
  );
}

export default Note;
