import React from "react";

function Typing(props) {
  const handleChange = (event) => {
    props.setSelectedText(event.target.value.substring(event.target.selectionStart, event.target.selectionEnd));
  };

  return (
    <textarea
      id="body"
      className="note-body"
      placeholder="Your Note Here"
      onChange={handleChange}
      value={props.value}
    />
  );
}

export default Typing;
