import EditButtons from "./EditButtons";
import { useRef, useState } from "react";

function Typing(props) {
  const [content, setContent] = useState("");
  const textRef = useRef(null);

  function handleButtonClick(formatting, value) {
    switch (formatting) {
      case "font-family":
        document.execCommand("fontName", false, value);
        break;
      case "bold":
        document.execCommand("bold", false, null);
        break;
      case "italic":
        document.execCommand("italic", false, null);
        break;
      case "underline":
        document.execCommand("underline", false, null);
        break;
      case "link":
        const link = prompt("Enter the link:");
        document.execCommand("createLink", false, link);
        break;
      case "ordered-list":
        document.execCommand("insertOrderedList", false, null);
        break;
      case "unordered-list":
        document.execCommand("insertUnorderedList", false, null);
        break;
      case "remove-formatting":
        document.execCommand("removeFormat", false, null);
        break;
      default:
        break;
    }
    textRef.current.focus();
  }

  function handleKeyDown(e) {
    // Check if the user has pressed Enter key
    if (e.keyCode === 13) {
      // Prevent the default behavior of the Enter key
      e.preventDefault();
      // Add a newline character to the content state
      setContent((prevContent) => prevContent + "\n");
    }
  }

  return (
    <div>
      <EditButtons handleButtonClick={handleButtonClick} />
      <div
        onKeyDown={handleKeyDown}
        contentEditable
        ref={textRef}
        style={{
          padding: "10px",
          border: "none",
          outline: "transparent",
          minWidth: "90%",
          minHeight: "99%",
        }}
        dangerouslySetInnerHTML={{ __html: content }}
      />
    </div>
  );
}

export default Typing;
