import React, { useState } from "react";

function Typing() {
  const [text, setText] = useState("");

  const handleChange = (event) => {
    setText(event.target.value);
  };

  const handleBoldClick = () => {
    const textarea = document.getElementById("body");
    const selection = window.getSelection();
    const range = selection.getRangeAt(0);
    const startOffset = range.startOffset;
    const endOffset = range.endOffset;
    const selectedText = textarea.value.substring(startOffset, endOffset);
    const newText = `${textarea.value.substring(0, startOffset)}<b>${selectedText}</b>${textarea.value.substring(endOffset)}`;
    textarea.value = newText;
    setText(newText);
  };

  return (
    <div>
      <textarea id="body" className="note-body" placeholder="Your Note Here" value={text} onChange={handleChange} />
      <button id="boldButton" style={{ fontWeight: "bold" }} onClick={handleBoldClick}>B</button>
      <button id="italicButton" style={{ fontStyle: "italic" }}>I</button>
      <button id="underlineButton" style={{ textDecoration: "underline" }}> U</button>
    </div>
  );
}

export default Typing;
