import { useNavigate } from 'react-router-dom';

function Note({notes, onDeleteNote, activeNote}) {
  console.log(activeNote)
  const navigate = useNavigate();
  const { id, title, lastModified, body } = activeNote || {};
  function handleEditClick() {
    navigate(`/${id}/edit`);
  }

  return (  
    <div className="note-page">
      <div className="note-head">
        <div style={{ display: "flex", flexDirection: "column"}}>
          <p id="title">{title}</p>
          <small>
            {lastModified}
          </small>
        </div>
        {notes.map((note) => (
          <div>
            <button id="EditButton" onClick={handleEditClick}>Edit</button>
            <button id="deleteButton" onClick={() => onDeleteNote(note.id)}>Delete</button>
          </div>
        ))}
      </div>
      <div className="note-body">
        {body}
      </div>
    </div>
  );
}


export default Note;
