import React from 'react';
import NoteList from './NoteList';
import { useState } from 'react';
import uuid from 'react-uuid';
import Note from './Note';
import EditNote from './EditNote';

function Layout() {
  const [notes, setNotes] = useState([]);
  const [activeNote, setActiveNote] = useState(null);

  function handleAddNote() {
    const newNote = {
      id: uuid(),
      title: 'Untitled',
      body: '...',
      lastModified: null,
    };
    setNotes([newNote, ...notes]);
    setActiveNote(newNote.id);
  }

  function handleDeleteNote(id) {
    setNotes(notes.filter((note) => note.id !== id));
    setActiveNote(null);
  }

  function handleNoteClick(id) {
    setActiveNote(id);
  }

  function handleNoteChange(updatedNote) {
    const updatedNotes = notes.map((note) =>
      note.id === updatedNote.id ? updatedNote : note
    );
    setNotes(updatedNotes);
  }

  return (
    <div className='container'>
      <div className='top'>
        <div className='button'>
          <button id="list">â‰¡</button>
        </div>
        <div className='name'>
          <h1>Lotion</h1>
          <small>Like Notion, but worse.</small>
        </div>
      </div>
      <div className='page'>
        <div className='list'>
        <NoteList notes={notes} onAddNote={handleAddNote} activeNote={activeNote} onNoteClick={handleNoteClick} />
        </div>
        {activeNote ? (
          <Note notes={notes} activeNote={activeNote} onDeleteNote={handleDeleteNote} onNoteChange={handleNoteChange} />
        ) : (
          <div className='no-note-selected'>No note selected</div>
        )}
          {activeNote && (
          <EditNote notes={notes} activeNote={activeNote} onNoteChange={handleNoteChange} onDeleteNote={handleDeleteNote} />
        )}

      </div>
    </div>                  
  );
}

export default Layout;


