function NoteList({notes, onAddNote}){
  

  return (
    <div className='notebar'>
      <div className='list-top'>
        <h2>Notes</h2>
        <button className="add" onClick={onAddNote}>+</button>
      </div>
      <div className='list-notes'>
        {notes.map((note) => (
          <div className='list-note' key={note.id}>
            <div className='note-title'>
              <strong>{note.title}</strong> <br></br>
              <small>{note.lastModified && new Date().toLocaleDateString('en-US', {
                month: 'long',
                day: '2-digit',
                year: 'numeric',
                hour: 'numeric',
                minute: 'numeric',
                hour12: true,
              })}</small>
            </div>
            <p>{note.body && note.body.substr(0,100) + "..."}</p> 
          </div>  //need to play around with line above
         ))}
      </div>
    </div>
  );
}

export default NoteList;
