import React, { useState, useEffect } from 'react';
import NoteList from './NoteList';
import Note from './Note';
import EditNote from './EditNote';
import { useLocation } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

function Layout() {
  const [notes, setNotes] = useState([]);
  const [activeNote, setActiveNote] = useState(null);
  const [counter, setCounter] = useState(1);
  const location = useLocation();
  const [isListVisible, setIsListVisible] = useState(true);
  const navigate = useNavigate();

  // Save notes to browser history on add/edit
  useEffect(() => {
    if (notes.length > 0) {
      const stateObj = { notes };
      const title = '';
      window.history.pushState(stateObj, title);
    }
  }, [notes]);

  // Retrieve notes from browser history on load
  useEffect(() => {
    const storedState = window.history.state;
    if (storedState && storedState.notes) {
      setNotes(storedState.notes);
      setCounter(storedState.notes.length + 1);
    }
  }, []);

  function handleAddNote() {
    const newNote = {
      id: counter,
      title: 'Untitled',
      body: '',
      lastModified: null,
    };
    setNotes([newNote, ...notes]);
    setActiveNote(newNote.id);
    setCounter(counter + 1);
    navigate(`/${newNote.id}/edit`);
  }

  function handleDeleteNote(id) {
    setNotes(notes.filter((note) => note.id !== id));
    setActiveNote(null);
  }

  function handleNoteClick(id) {
    setActiveNote(id);
  }

  function handleNoteChange(updatedNote) {
    const updatedNotes = notes.map((note) =>
      note.id === updatedNote.id ? updatedNote : note
    );
    setNotes(updatedNotes);
  }

  const isEditing = location.pathname.endsWith('/edit');



  return (
    <div className='container'>
      <div className='top'>
        <div className='button'>
          <button id='list' onClick={() => setIsListVisible(!isListVisible)}>â‰¡</button>
        </div>
        <div className='name'>
          <h1>Lotion</h1>
          <small>Like Notion, but worse.</small>
        </div>
      </div>
      <div className='page'>
        {isListVisible && (
          <div className='list'>
            <NoteList
              notes={notes}
              onAddNote={handleAddNote}
              activeNote={activeNote}
              onNoteClick={handleNoteClick}
            />
          </div>
        )}
        {activeNote ? (
          isEditing ? (
            <EditNote
              notes={notes}
              activeNote={activeNote}
              onNoteChange={handleNoteChange}
              onDeleteNote={handleDeleteNote}
            />
          ) : (
            <Note
              notes={notes}
              activeNote={activeNote}
              onDeleteNote={handleDeleteNote}
            />
          )
        ) : (
          <div className='no-note'>Select a note, or create a new one.</div>
        )}
      </div>
    </div>
  );
}

export default Layout;
