import React, { useState } from "react";
import CalendarSelector from "./CalendarSelector";
import Typing from "./Typing";

function Note() {
  const [selectedDate, setSelectedDate] = useState(new Date());

  function handleDateChange(date) {
    setSelectedDate(date);
  }

  function handleTodayButtonClick() {
    setSelectedDate(new Date());
  }

  function handleFontChange(e) {
    document.execCommand("fontName", false, e.target.value);
  }

  function handleBoldClick() {
    document.execCommand("bold", false, null);
  }

  function handleItalicClick() {
    document.execCommand("italic", false, null);
  }

  function handleUnderlineClick() {
    document.execCommand("underline", false, null);
  }

  function handleLinkClick() {
    const url = prompt("Enter the URL:");
    document.execCommand("createLink", false, url);
  }

  function handleOrderedListClick() {
    document.execCommand("insertOrderedList", false, null);
  }

  function handleUnorderedListClick() {
    document.execCommand("insertUnorderedList", false, null);
  }

  return (  
    <div className="note-page">
      <div className="note-head">
        <div style={{ display: "flex", flexDirection: "column"}}>
          <input type="text" id="title" placeholder="Untitled" style={{ border: "none", outline: "none" }} />
          <small>
            <CalendarSelector selectedDate={selectedDate} handleDateChange={handleDateChange} handleTodayButtonClick={handleTodayButtonClick} />
          </small>
        </div>
        <div>
          <button id="saveButton">Save</button>
          <button id="deleteButton">Delete</button>
        </div>
      </div>
      <div className="note-body">
        <Typing 
          onFontChange={handleFontChange} 
          onBoldClick={handleBoldClick} 
          onItalicClick={handleItalicClick} 
          onUnderlineClick={handleUnderlineClick} 
          onLinkClick={handleLinkClick} 
          onOrderedListClick={handleOrderedListClick} 
          onUnorderedListClick={handleUnorderedListClick} 
        />
      </div>
    </div>
  );
}

export default Note;
