import React from 'react';

function Note({ notes, activeNote, onDeleteNote }) {
  const activeNoteData = notes.find((note) => note.id === activeNote);

  if (!activeNoteData) {
    return <div className='no-note'>Select a note, or create a new one.</div>;
  }

  const { title, body, lastModified } = activeNoteData;

  return (
    <div className='note'>
      <div className='note-top'>
        <div className='note-title'>
          <h3>{title}</h3>
          <small>{lastModified}</small>
        </div>
        <div className='note-delete'>
          <button onClick={() => onDeleteNote(activeNote)}>Delete</button>
        </div>
      </div>
      <div className='note-body' dangerouslySetInnerHTML={{ __html: body }} />
    </div>
  );
}

export default Note;
