import React from 'react';
import { useNavigate } from 'react-router-dom';

function Note({ notes, onDeleteNote, activeNote}) {
  const navigate = useNavigate();
  const note = notes.find((note) => note.id === activeNote);
  const date = new Date(note.lastModified);
  const dateOptions = {
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "numeric",
    minute: "numeric",
    hour12: true,
  };

  function handleEditClick() {
    navigate(`/${activeNote}/edit`);
  }

  function handleDeleteClick() {
    onDeleteNote(activeNote);
    navigate('/');
  }

  if (!note.lastModified) {
    note.lastModified = note.lastModified.toLocaleString("en-US", dateOptions);  
  }

  return (
    <div className="note-page">
      <div className="note-head">
        <div style={{ display: "flex", flexDirection: "column"}}>
          <p id="title">{note.title}</p>
          <small>
            {note.lastModified}
          </small>
        </div>
        <div>
          <button id="editButton" onClick={handleEditClick}>Edit</button>
          <button id="deleteButton" onClick={handleDeleteClick}>Delete</button>
        </div>
      </div>
      <div className="note-body">
        <p>{note.body}</p>
      </div>
    </div>
  );
}

export default Note;
